import { useState } from 'react';
import './App.css';
import Data from './fruits.json';

function App() {
  const [query, setQuery] = useState("");
  return (
    <div className="App">
      <input placeholder='search...' type='text' className='search' />
        {Data.map((fruit, index)=>{
          return(
          <>
            <div>
              <img src={fruit.img} alt={fruit.name} width='300px' className='listItem' />
            </div>
          </>
          )
        })}
    </div>
  );
}

export default App;
