  const search = (data)=>{
    return Data.filter(fruit=>fruit.name.toLocaleLowerCase().toLocaleUpperCase().includes(query));
  }

  return (
    <div className="App">
      <input placeholder='search...' type='text' className='search' onChange={(e)=> setQuery(e.target.value)} />

        {Data.map((fruit)=>{
          return(
          <>
            <div>
              <img src={fruit.img} alt={fruit.name} width='300px' className='listItem' data={search(Data)} />
            </div>
          </>
          )
        })}

    </div>
  );
}

export default App;
